jQuery(document).ready(function($) {
    var currentCategory = 'all';

    $('.images-filter__heading a').on('click', function(e) {
        e.preventDefault();
        
        var filterValue = $(this).data('filter');
        currentCategory = filterValue;
        
 
        $('.images-filter__heading a').removeClass('active');
        $(this).addClass('active');

        loadImages(filterValue, 1);
    });
    

    $(document).on('click', '.pagination-wp a', function(e) {
        e.preventDefault();
        
        var page = $(this).data('page');
        loadImages(currentCategory, page);
    });
    
    function loadImages(category, page) {
        $.ajax({
            url: ajax_object.ajax_url,
            type: 'POST',
            data: {
                action: 'filter_images',
                category_id: category,
                page: page,
                nonce: ajax_object.nonce
            },
            success: function(response) {
                if (response.success) {
                    $('#images-container').html(response.data.html);
                    
                    if (response.data.show_pagination && response.data.pagination) {
                        $('#pagination-container').html(response.data.pagination).show();
                    } else {
                        $('#pagination-container').hide();
                    }
                }
            },
            error: function() {
                console.log('AJAX Error');
            }
        });
    }
});

